class Solution(object):
    def getHint(self, secret, guess):
        """
        :type secret: str
        :type guess: str
        :rtype: str
        """
        s_list = list(secret)
        g_list = list(guess)
        s_dic = {}
        bull = cow = 0

        for idx in xrange(len(s_list)):
            if s_list[idx] == g_list[idx]:
                bull += 1
                s_list[idx] = None
                g_list[idx] = None
        
        for ch in s_list:
            if ch:
                if ch in s_dic:
                    s_dic[ch] += 1
                else:
                    s_dic[ch] = 1

        for ch in g_list:
            if ch and ch in s_dic and s_dic[ch]:
                cow += 1
                s_dic[ch] -= 1

        return "{}A{}B".format(bull, cow)

