class Solution(object):
    def shortestBridge(self, A):
        """
        :type A: List[List[int]]
        :rtype: int
        """
        rows = len(A)
        cols = len(A[0])
        found = False
        queue = [None]
        borders = []
        res = 0

        for row in range(rows):
            if found:
                break

            for col in range(cols):
                if A[row][col]:
                    A[row][col] = 2
                    queue[0] = (row, col)
                    found = True
                    break

        while queue:
            temp_queue = []

            for r, c in queue:
                if r - 1 > -1:
                    if A[r - 1][c] == 1:
                        temp_queue.append((r - 1, c))
                        A[r - 1][c] = 2
                    else:
                        borders.append((r - 1, c))

                if r + 1 < rows:
                    if A[r + 1][c] == 1:
                        temp_queue.append((r + 1, c))
                        A[r + 1][c] = 2
                    else:
                        borders.append((r + 1, c))

                if c - 1 > -1:
                    if A[r][c - 1] == 1:
                        temp_queue.append((r, c - 1))
                        A[r][c - 1] = 2
                    else:
                        borders.append((r, c - 1))

                if c + 1 < cols:
                    if A[r][c + 1] == 1:
                        temp_queue.append((r, c + 1))
                        A[r][c + 1] = 2
                    else:
                        borders.append((r, c + 1))

            queue = temp_queue

        while borders:
            temp_borders = []
            res += 1

            for r, c in borders:
                A[r][c] = 2

                if r - 1 > -1:
                    if A[r - 1][c] == 1:
                        return res
                    elif A[r - 1][c] == 0:
                        temp_borders.append((r - 1, c))
                        A[r - 1][c] = 2

                if r + 1 < rows:
                    if A[r + 1][c] == 1:
                        return res
                    elif A[r + 1][c] == 0:
                        temp_borders.append((r + 1, c))
                        A[r + 1][c] = 2

                if c - 1 > -1:
                    if A[r][c - 1] == 1:
                        return res
                    elif A[r][c - 1] == 0:
                        temp_borders.append((r, c - 1))
                        A[r][c - 1] = 2

                if c + 1 < cols:
                    if A[r][c + 1] == 1:
                        return res
                    elif A[r][c + 1] == 0:
                        temp_borders.append((r, c + 1))
                        A[r][c + 1] = 2

            borders = temp_borders

        # for r, c in borders:
        #     queue = [(r, c)]
        #     visited = set([(r, c)])
        #     dist = 0
        #     found = False

        #     while not found:
        #         temp_queue = []
        #         dist += 1

        #         for tr, tc in queue:
        #             if tr - 1 > -1 and (tr - 1, tc) not in visited:
        #                 if A[tr - 1][c] == 1:
        #                     found = True
        #                     break
        #                 else:
        #                     visited.add((tr - 1, tc))
        #                     temp_queue.append((tr - 1, tc))

        #             if tr + 1 < rows and (tr + 1, tc) not in visited:
        #                 if A[tr + 1][tc] == 1:
        #                     found = True
        #                     break
        #                 else:
        #                     visited.add((tr + 1, tc))
        #                     temp_queue.append((tr + 1, tc))

        #             if tc - 1 > -1 and (tr, tc - 1) not in visited:
        #                 if A[tr][tc - 1] == 1:
        #                     found = True
        #                     break
        #                 else:
        #                     visited.add((tr, tc - 1))
        #                     temp_queue.append((tr, tc - 1))

        #             if tc + 1 < cols and (tr, tc + 1) not in visited:
        #                 if A[tr][tc + 1] == 1:
        #                     found = True
        #                     break
        #                 else:
        #                     visited.add((tr, tc + 1))
        #                     temp_queue.append((tr, tc + 1))

        #         queue = temp_queue

        #     if dist == 1:
        #         return 1

        #     res = min(res, dist)

        return res


